use actix_multipart::Multipart;
use actix_web::{web, App, HttpResponse, HttpServer, Result};
use base64::{engine::general_purpose, Engine as _};
use futures_util::StreamExt as _;

async fn create_page() -> Result<HttpResponse> {
    let form = r#"
  <!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>File Upload Form</title>
  <meta name="viewport" content="width=device-width, initial-scale=1">
  <style>
  @font-face { 
    font-family: "myTerminus";
    src: url(data:application/octet-stream;base64,);
  }
    body {
      font-family: 'myTerminus', cursive;
      margin: 0;
      background-color: #000;
      color: #fff;
    }

    header {
      background-color: #000;
      color: #fff;
      padding: 1em;
      border: 2px solid #fff;
      border-radius: 5px;
      text-align: center;
      text-transform: uppercase;
    }

    h1 {
      margin: 0;
      font-size: 5em;
    }

    form {
      margin: 2em auto;
      max-width: 500px;
      padding: 2em;
      border: 2px solid #fff;
      border-radius: 5px;
      text-align: center;
    }

    label {
      display: block;
      margin-bottom: 0.5em;
      font-size: 2em;
      font-weight: bold;
      text-transform: uppercase;
    }

    input[type="text"],
    input[type="file"] {
      display: block !important;
      font-family: 'myTerminus', cursive;
      margin-bottom: 1em;
      padding: 0.5em;
      border: 2px solid #fff;
      border-radius: 5px;
      font-size: 2em;
      width: 90%;
      background-color: #000;
      color: #fff;
      text-transform: uppercase;
    }

    input[type="file"] {
      height: auto;
      display: block !important;
    }

    input[type="file"]::file-selector-button{
      font-family: 'myTerminus', cursive;
      display: block !important;
      width: 100%;
      margin-bottom: 1em;
      padding: 0.5em;
      border: 2px solid #fff;
      border-radius: 5px;
      font-size: 1em;
      background-color: #000;
      color: #fff;
      text-transform: uppercase;
    }

    input[type="file"]::file-selector-button:hover{
      background-color: #fff;
      color: #000;
    }

    input[type="submit"] {
      display: block;
      background-color: #7F00FF;
      width: 97%;
      color: #fff;
      border: none;
      border-radius: 5px;
      padding: 0.5em 1em;
      font-size: 3em;
      cursor: pointer;
      text-transform: uppercase;
      font-family: 'myTerminus', cursive;
      letter-spacing: 2px;
      box-shadow: 2px 2px 0px #fff;
    }

    input[type="submit"]:hover {
      background-color: #fff;
      color: #7F00FF;
      box-shadow: 2px 2px 0px #7F00FF;
    }
    
    /* Media queries */
    
    @media only screen and (max-width: 680px) {
      header {
        font-size: 1.5em;
        max-width: 100%;
      }
      h1 {
        font-size: 1.5em;
      }
      form {
        max-width: 90%;
      }
      label,
      input[type="text"],
      input[type="file"],
      input[type="submit"] {
        display: block !important;
        font-size: 0.8em;
        padding: 0.3em;
        margin-bottom: 0.8em;
      }
      input[type="submit"] {
        padding: 0.5em 1em;
        height: auto;
        font-size: 1.2em;
      }
    }
    </style>
  </head>
  <body>
    <header>
      <h1>File Upload Form</h1>
    </header>
    <form action="/" method="POST" enctype="multipart/form-data">
      <label for="body">Body:</label>
      <input type="text" name="body" id="body">
      <label for="attachments">Attachments:</label>
      <input type="file" name="attachments" id="attachments" multiple>
      <input type="submit" value="Submit">
    </form>
  </body>
  </html>
"#;

    Ok(HttpResponse::Ok().content_type("text/html").body(form))
}

// function to take post request and return html page
async fn build_page(mut payload: Multipart) -> Result<HttpResponse> {
    let mut body = String::new();
    let mut images = Vec::new();

    while let Some(item) = payload.next().await {
        let mut field = item?;
        let content_disposition = field.content_disposition();
        let name = content_disposition.get_name().unwrap_or("");

        if name == "body" {
            while let Some(chunk) = field.next().await {
                if let Ok(data) = chunk {
                    let data_string = String::from_utf8_lossy(&data);
                    body = data_string.to_string();
                }
            }
        } else if name == "attachments" {
            let mut data = Vec::new();
            while let Some(chunk) = field.next().await {
                let chunk = chunk?;
                data.extend_from_slice(&chunk);
            }

            let content_type = field.content_type().expect("REASON").to_string();
            let data_uri = format!(
                "data:{};base64,{}",
                content_type,
                general_purpose::STANDARD_NO_PAD.encode(&data)
            );

            if !&data.is_empty() {
                images.push(format!(
                    "<img src=\"{}\" style=\"margin-top: 1rem; margin-bottom: 1rem;\">",
                    data_uri
                ));
            }
        } else {
            println!("Field '{}' not processed", name);
        }
    }

    let images_html = images.join("\n");

    let html = format!("
      <!DOCTYPE html>
      <html>
      <head>
        <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\" />
        <title>Message</title>
        <style>
          html {{
            background-color: white;
            font-family: \"Roboto\", sans-serif;
            font-size: 20px;
          }}
          a {{ font-weight: bold }}
          img {{ max-width: 100%; }}
          .background {{
            background-size: cover;
            margin-top: 52px; 
            margin-left: auto;
            margin-right: auto;
            margin-bottom: 0;
            padding: 52px 24px 48px 24px;
            text-align: left;
            box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.25);
            border-radius: 10px;
            max-width: 752px;
            background-image: url('data:image/svg+xml;base64,');
          }}
          .text {{
            color: #000000;
            padding: 24px;
            margin-top: 48px;
            margin-bottom: auto;
            background: rgba(255, 255, 255, 0.9);
            border-radius: 4px;
          }}
          .signature {{
            margin-top: 48px;
            margin-bottom: 0;
            color: #142636;
            display: flex;
            font-size: 20px;
            height: 34px;
            align-items: center;
            justify-content: center;
            text-align: center;
            line-height: 35px;
          }}
          .signature img {{
              vertical-align: middle;
              height: 34px;
              margin-left: 8px;
          }}
          @media (max-width: 831px) {{
            .background {{
              margin-top: 24px;
              margin-left: 16px;
              margin-right: 16px;
              width: calc(100vw-32px);
            }}
            .signature {{
              display: block;
              margin-bottom: 24px;
            }}
          }}
          @media (prefers-color-scheme: dark) {{
            html {{
              background-color: #142636;
              color: #fff;
            }}
            .text {{
              background-color: #142636;
              color: #fff;
            }}
          }}
        </style>
      </head>
      <body>
        <div class=\"background\">
          <div class=\"text\">
            <p>{}</p>
            {}
          </div>
          <div class=\"signature\">
            This message was sent using <img alt=\"SchoolStatus Logo\" src=\"data:image/svg+xml;base64,\" />
            </div>
        </div>
      </body>
    </html>", body, images_html);

    Ok(HttpResponse::Ok().content_type("text/html").body(html))
}

#[actix_web::main]
async fn main() -> std::io::Result<()> {
    HttpServer::new(move || {
        App::new()
            .route("/", web::get().to(create_page))
            .route("/", web::post().to(build_page))
    })
    .bind("0.0.0.0:8000")?
    .run()
    .await
}
